mapper.select:
SELECT COUNT(*)
FROM member
WHERE m_nick=#{m_nick}

SELECT COUNT(*)
FROM member
WHERE m_tel=#{m_tel}

SELECT m_name,m_email
FROM member
WHERE m_tel=#{m_tel}

SELECT m_pwd,m_email
FROM member
WHERE m_tel=#{m_tel}

SELECT COUNT(*)
FROM (select m_nick from member WHERE NOT m_nick=#{mynick})
WHERE m_nick=#{m_nick}

mapper.update:
UPDATE member
	
		m_nick=#{m_nick}
		, m_companyno=#{m_companyno}
		, m_profile=#{m_profile}
	
	WHERE m_email = #{m_email}

UPDATE member
SET m_pwd = #{pwd}
WHERE m_email = #{m_email}

UPDATE member
SET m_tel = #{tel}
WHERE m_email = #{m_email}

mapper.select:
		SELECT CEIL(COUNT(*)/5)
        FROM (SELECT * FROM cart)
        WHERE m_email=#{m_email}

		SELECT CEIL(COUNT(*)/5)
        FROM (SELECT * FROM board)
        WHERE m_email=#{m_email}

		SELECT CEIL(COUNT(*)/5)
        FROM (SELECT * FROM board_comment)
        WHERE m_email=#{m_email}

		SELECT CEIL(COUNT(*)/5)
        FROM (SELECT * FROM event_cart)
        WHERE m_email=#{m_email}

SELECT e_no,e_name,e_regdate,e_enddate,e_content
FROM event
WHERE e_no=#{e_no}

mapper.update:
		UPDATE event SET e_name=#{e_name},e_regdate=#{e_regdate}
		,e_enddate=#{e_enddate},e_content=#{e_content}
		WHERE e_no=#{e_no}
