mapper.select:
SELECT COUNT(*)
FROM (SELECT * FROM board)
WHERE b_grade=#{grade}

mapper.update:
UPDATE board SET
b_hit = b_hit + 1
WHERE b_no = #{b_no} AND b_grade=#{grade}

mapper.insert:

	SELECT NVL(MAX(b_no)+1,1) as b_no FROM board

INSERT INTO board VALUES(
	#{b_no}, #{b_subject}, #{m_email}, SYSDATE, #{b_content}, #{b_grade}, 0 , #{b_img_cnt}
)
